import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Login from './components/Login';
import FarmerDashboard from './components/FarmerDashboard';
import DistributorDashboard from './components/DistributorDashboard';
import RetailerDashboard from './components/RetailerDashboard';
import ConsumerDashboard from './components/ConsumerDashboard';
import QRScanner from './components/QRScanner';
import Unauthorized from './components/Unauthorized';
import ProtectedRoute from './routes/ProtectedRoute';

function App() {
  const [role, setRole] = useState(null);

  useEffect(() => {
    const storedRole = localStorage.getItem("role");
    if (storedRole) setRole(storedRole);
  }, []);

  const handleLogin = (newRole) => {
    setRole(newRole);
    localStorage.setItem("role", newRole);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    setRole(null);
  };

  return (
    <Router>
      <div>
        {role && <button onClick={handleLogout}>Logout</button>}
        <Routes>
          <Route path="/login" element={<Login setRole={handleLogin} />} />
          <Route path="/unauthorized" element={<Unauthorized />} />

          <Route element={<ProtectedRoute role="farmer" userRole={role} />}>
            <Route path="/farmer" element={<FarmerDashboard />} />
          </Route>

          <Route element={<ProtectedRoute role="distributor" userRole={role} />}>
            <Route path="/distributor" element={<DistributorDashboard />} />
          </Route>

          <Route element={<ProtectedRoute role="retailer" userRole={role} />}>
            <Route path="/retailer" element={<RetailerDashboard />} />
          </Route>

          <Route element={<ProtectedRoute role="consumer" userRole={role} />}>
            <Route path="/consumer" element={<ConsumerDashboard />} />
          </Route>

          {/* Default redirect */}
          <Route path="*" element={role ? <Navigate to={`/${role}`} /> : <Navigate to="/login" />} />
        </Routes>

        {/* Always show QR Scanner */}
        <QRScanner />
      </div>
    </Router>
  );
}

export default App;
